mapper.select:
select *
from question
where creator = #{userId} limit #{offset}
    , #{size}

select count(*)
from question
where creator = #{userId}

mapper.update:
update question
set title=#{title},
    description=#{description},
    gmt_modified=#{gmtModified},
    tag=#{tag}
where id = #{id}

update question
set view_count=view_count+#{viewCount}
where id = #{id}

mapper.select:
select *
from question
where id != ${id} and tag regexp #{tag}

select * from question


        and title regexp #{search}


order by gmt_create desc  limit #{offset},#{size}
